function createPassword() {
    var text = "";
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (var i = 0; i < 9; i++) {
        text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
}

router.post("/api/login/", async (req, res) => {
    try {
        const { email, pass } = req.body;
        if (!email) {
            response.error(req, res, true, false, "Ingrese un correo", 401, "Ingrese un correo");
        } else if (!pass) {
            response.error(req, res, true, false, "Ingrese una contrase침a", 401, "Ingrese una contrase침a");
        } else {
            mysqlConection.query('SELECT * FROM users WHERE correo = ?', [email], async (error, results) => {
                if (results.length == 0) {
                    response.error(req, res, true, false, "Correo/ contrase침a invalida", 401, "Correo/ contrase침a invalida");
                } else if (!(await bcryptjs.compare(pass, results[0].pass))) {
                    response.error(req, res, true, false, "Correo/ contrase침a invalida", 401, "Correo/ contrase침a invalida");
                } else {
                    const id = results[0].id
                    const token = jwt.sign({ id: id }, "secret_token", {
                        expiresIn: "8h"
                    })
                    const cookiesOptions = {
                        expires: new Date(Date.now() + 1 * 24 * 60 * 60 * 1000),
                        httpOnly: true
                    }
                    let profile = results[0].tipo;
                    let emailUser = email;
                    let nameUser = results[0].nombre
                    let body = {
                        'is_security': token,
                        'is_Profile': profile,
                        'is_Emial': emailUser,
                        'is_Name': nameUser,
                        "success": true,
                        "mess": "Bienvenido " + results[0].nombre,
                        expires: cookiesOptions.expires
                    }
                    response.success(req, res, false, true, body, 200)
                }
            })
        }
    } catch (error) {
    }
});

